mapper.select:
SELECT mem_id, mem_type, mem_age, mem_email, mem_name, mem_ca, mem_auth
FROM member
WHERE mem_id=#{mem_id}

mapper.update:
UPDATE member 
SET mem_name=#{mem_name}, mem_type=${mem_type}, mem_age=#{mem_age}, mem_email=#{mem_email}, mem_ca=${mem_ca}, mem_auth=${mem_auth}
WHERE mem_id=#{mem_id}
